/*
 * Very lightweight jquery lightbox plugin. Supports custom content eq youtube embeded videos.
 * 
 * by Henri Abel
 *
 * More information in:
 * http://www.github.com/henriAbel/
 */

(function ($) {

    $.fn.popup = function(options) {

        var settings = $.extend({
            getContentUrl: function(e) {
                return e.attr('data-url');
            },
            getContentType: function(e) {
                type = e.attr('data-type');
                return type !== undefined ? type : 'image';
            },
            fittocontent: true
        }, options );

        $this = $(this);
        var self = this;
        var container,
            content,
            dummy,
            wrapper;
        var opened = false;
        var active;

        // Remove old bindings if exists
        this.unbind('click.popup');

        var showContainer = function() {
            if (!opened) {
                beforeOpen();
                container.show();
                container.css('opacity', '1');
                opened = true;    
            }
        }

        this.close = function() {
            container.css('opacity', '0');
            content.empty();
            setTimeout(function() {
                container.css('display', 'none');
                wrapper.css({
                    'width':     '160px',
                    'height':    '100px'
                });
            }, 300);
            opened = false;
            afterClose();
        }

        var hasNext = function() {
            return getNext().length > 0;
        }

        var getNext = function() {
            return active.next($this.selector);
        }

        var hasPrevious = function() {
            return getPrevious().length > 0;
        }

        var getPrevious = function() {
            return active.prev($this.selector);
        }

        var setActive = function(element) {
            active = element;
            url = settings.getContentUrl(active);
            type = settings.getContentType(active);
            if (opened) {
                content.empty();
            }
            else {
                showContainer();
            }
            dummy.show();
            switch(type) {
                case 'image':
                    preload(url, function(e) {
                        e.style.maxWidth = '100%';
                        e.style.maxHeight = '100%';
                        // Check if need to scale wrapper 
                        var w = $(window).width() * 0.9;
                        var h = $(window).height() * 0.9;
                        var pw = this.width;
                        var ph = this.height;
                        var ratio = pw / ph;

                        if (ph > h) {
                            ph = h;
                            pw = ratio * h;
                        }
                        
                        if (pw > w) {
                            pw = w;
                            ph = pw / ratio;
                        }

                        wrapper.css({
                            'width': pw,
                            'height': ph
                        });
                        setContent(e);
                    });
                    break;
                case 'youtube':
                    var yt = document.createElement('div');
                    content.append(yt);
                    wrapper.css({
                        'width': 640,
                        'height': 480
                    });
                    var player = new YT.Player(yt, {
                        videoId: url,
                        width: 640,
                        height: 480,
                        playerVars: { 'autoplay': 1, 'controls': 1 },
                        events: {
                            'onReady': function(e) {
                                setContent(function() {
                                    dummy.hide();
                                });
                            }
                        }
                    });
                    break;
            }
            
        }

        var setContent = function(e) {
            // Wait CSS transition complete 
            setTimeout(function() {
                if (typeof e == 'function ') {
                    e.call(this);
                }
                else {
                    content.html(e);
                    dummy.hide();    
                }
            }, 300);
        }

        var createContainer = function() {
            var old = document.getElementById('popup-container');
            if (null != old) {
                old.parentNode.removeChild(old);    
            }

            econtainer = document.createElement('div');
            econtainer.setAttribute('class', 'text-center');
            econtainer.setAttribute('id', 'popup-container');
            estyle = econtainer.style;
            estyle.zIndex = '10000'; // Twitter bootstrap menus are z-index 1000
            //estyle.display = 'none';
            estyle.width = '100%';
            estyle.height = '100%';
            estyle.position = 'fixed';
            estyle.top = 0;
            estyle.opacity = '0';
            estyle.display = 'none';
            estyle.filter = 'alpha(opacity=0)'; //For IE
            doVendor(estyle, 'all 0.3s ease');

            c = document.createElement('div');
            style = c.style;
            style.display = 'inline-block';
            style.zIndex = '10001';
            style.position = 'absolute';
            style.left = '0';
            style.top = '0';
            style.right = '0';
            style.bottom = '0';
            style.margin = 'auto';
            style.maxWidth = '90%';
            style.maxHeight = '90%';
            style.width = '160px';
            style.height = '100px';
            doVendor(style, 'all 0.3s ease');

            dummy = document.createElement('div');
            dummy.setAttribute('id', 'dummy');
            dstyle = dummy.style;
            dstyle.backgroundColor = '#ccc';
            dstyle.backgroundImage = "url('data:image/gif;base64,')";
            dstyle.backgroundPosition = '50% 50%';
            dstyle.backgroundRepeat = 'no-repeat';
            dstyle.position = 'absolute';
            dstyle.zIndex = '-1';
            dstyle.width = '100%';
            dstyle.height = '100%';

            ccontainer = document.createElement('div');
            ccontainer.setAttribute('id', 'popup-content');
            econtainer.appendChild(c);
            c.appendChild(dummy);
            c.appendChild(ccontainer);

            b = document.createElement('div');
            b.setAttribute('id', 'popup-background');
            bstyle = b.style;
            bstyle.backgroundColor = 'black';
            bstyle.opacity = '.7';
            bstyle.width = '100%';
            bstyle.height = '100%';
            bstyle.top = 0;
            bstyle.position = 'absolute';
            bstyle.filter = 'alpha(opacity=70)'; //For IE

            econtainer.appendChild(b);
            document.body.appendChild(econtainer);

            container = $(econtainer);
            wrapper = $(c);
            content = $(ccontainer);
            dummy = $(dummy);
        }

        var doVendor = function(element, content) {
            element.webkitTransition = content;
            element.MozTransition = content;
            element.msTransition = content;
            element.OTransition = content;
            element.transition = content;
        }

        var preload = function(src, callback) {
            image = new Image();
            image.onload = function() {
                callback.call(this, image);
            }
            image.onerror = function() {
                callback.call(this, image);
            }
            image.src = src;
        }

        var beforeOpen = function() {
            $(document).bind('keyup.popup', function(e) {
                switch(e.which) {
                    case 27: //Esc
                        self.close();
                        break;
                    case 39: // Arrow right
                        self.next();
                        break;
                    case 37: // Arrow left
                        self.prev();
                        break;
                    }
            });

            $(document).bind('click.popupOutside', function(e) {
                if (e.target.getAttribute('id') == 'popup-background') {
                    self.close();
                }
            });
        }

        var afterClose = function() {
            $(document).unbind('keyup.popup');
            $(document).unbind('click.popupOutside');
        }

        this.reload = function() {
            elements = $($this.selector);
            elements.unbind('click.popup');
            elements.bind('click.popup', function(e) {
                setActive($(e.target));
            });
        }

        this.next = function() {
            if (hasNext()) {
                setActive(getNext());    
            }
        }

        this.prev = function() {
            if (hasPrevious()) {
                setActive(getPrevious());    
            }
        }

        this.init = function() {
            this.bind('click.popup', function(e) {
                setActive($(e.target));
            });
            createContainer();
            var tag = document.createElement('script');
            tag.src = 'https://www.youtube.com/iframe_api';
            var firstScriptTag = document.getElementsByTagName('script')[0];
            firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

            return this;
        }
        
        return this.init();
    };
})(jQuery);

